defaultpen(linewidth(.7pt)+fontsize(8pt));
dotfactor=3;

pair A=(0,0), B=(-1,0), T=(2,0);
pair T0=T+10*dir(162);
pair[] RS=intersectionpoints(Circle(A,1),T--T0);
pair Sp=RS[0];
pair R=RS[1];
pair[] dots={A,B,T,Sp,R};
dot(dots);

